import React from 'react';
import { View, FlatList, Text, Image, StatusBar, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import styles from './styles';

export default function Products() {

  const navigation = useNavigation();
  function navigateToDetails(){
    navigation.navigate('Produtos', {screen: 'ProductDetails'});
  }

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <Text style={styles.title}>Produtos</Text>

      <FlatList
        style={styles.productsList}
        showsVerticalScrollIndicator = {false}
        keyExtractor = {product => String(product)}
        data={[1, 2, 3,4,5,6,7]}
        renderItem={() => (
          <TouchableOpacity onPress={() => navigateToDetails()} activeOpacity={0.8}>
            <View style={styles.product}>
              <Image
                style={{ width: 80, height: 80 }}
                source={{
                  uri: 'https://superprix.vteximg.com.br/arquivos/ids/175177-292-292/Maca-Fuji--1-unidade-aprox.-200g-.png?v=636294176957700000'
                }}
              />
              <View style={styles.productInfo}>
                <Text style={styles.productValue}>
                  <Text style={styles.productProperty}>Produto: </Text>
                Maçã
              </Text>
                <Text style={styles.productValue}>
                  <Text style={styles.productProperty}>Valor/KG: </Text>
                R$ 20,00
              </Text><Text style={styles.productValue}>
                  <Text style={styles.productProperty}>Valor/UN: </Text>
                R$ 2,00
              </Text>
              </View>

            </View>
          </TouchableOpacity>
        )}
      />

    </View>

  );
}
